body.messageBody:
@{concat('{
    "type": "AdaptiveCard",
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "width": "stretch",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "text": "', triggerBody()?['title'], '",
                                    "weight": "Bolder",
                                    "size": "Large"
                                }
                            ]
                        },
                        {
                            "type": "Column",
                            "width": "auto",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "text": "', triggerBody()?['timeFrame'], '",
                                    "horizontalAlignment": "Right",
                                    "isSubtle": true
                                }
                            ]
                        }
                    ]
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "', replace(triggerBody()?['overallSummary'], '"', '\"'), '",
            "wrap": true,
            "spacing": "Medium"
        },
        {
            "type": "FactSet",
            "facts": [
                {
                    "title": "Total Issues:",
                    "value": "', string(triggerBody()?['totalIssues']), '"
                },
                {
                    "title": "Critical Issues:",
                    "value": "', string(triggerBody()?['criticalIssues']), '"
                }
            ],
            "separator": true
        }', if(greater(triggerBody()?['criticalIssues'], 0), concat(',
        {
            "type": "TextBlock",
            "text": "Critical Issue Details",
            "weight": "Bolder",
            "size": "Medium",
            "separator": true
        },
        {
            "type": "Container",
            "items": [', join(body('Select_issue_cards'), ','), ']
        }'), ''), if(greater(triggerBody()?['totalIssues'], 0), concat(',
        {
            "type": "TextBlock",
            "text": "All Issues",
            "weight": "Bolder",
            "size": "Medium",
            "separator": true
        },
        {
            "type": "Container",
            "items": [', join(body('Select_all_issue_cards'), ','), ']
        }'), ''), '
    ],
    "actions": [
        {
            "type": "Action.OpenUrl",
            "title": "View Workflow Run",
            "url": "', triggerBody()?['workflowRunUrl'], '"
        }
    ]
}')}
